/* Team photo styling */
.team-photo-thumb {
    width: 60px;
    height: 60px;
    object-fit: cover;
    border-radius: 50%;
    border: 2px solid var(--bs-border-color);
}

.team-photo-display {
    width: 100px;
    height: 100px;
    object-fit: cover;
    border-radius: 50%;
    border: 2px solid var(--bs-border-color);
}

.team-photo-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
}

.team-photo-large {
    display: flex;
    justify-content: center;
    align-items: center;
}

/* Team cards */
.team-card {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    position: relative;
    overflow: hidden;
    max-height: 280px;
}

.team-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

.team-card.winner {
    border: 3px solid #ffd700;
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.5);
}

.team-card.runner-up {
    border: 3px solid #c0c0c0;
    box-shadow: 0 0 15px rgba(192, 192, 192, 0.5);
}

.team-card.third-place {
    border: 3px solid #cd7f32;
    box-shadow: 0 0 15px rgba(205, 127, 50, 0.5);
}

/* Score breakdown styling */
.score-breakdown {
    background: var(--bs-body-bg);
    border-radius: 0.3rem;
    padding: 0.5rem;
    border: 1px solid var(--bs-border-color);
    font-size: 0.85rem;
}

/* Confetti animation */
.confetti-piece {
    position: absolute;
    width: 10px;
    height: 10px;
    background: #ffd700;
    animation: confetti-fall 3s linear infinite;
    z-index: 1000;
}

.confetti-piece:nth-child(odd) {
    background: #ff6b6b;
    animation-delay: -0.5s;
}

.confetti-piece:nth-child(3n) {
    background: #4ecdc4;
    animation-delay: -1s;
}

.confetti-piece:nth-child(4n) {
    background: #45b7d1;
    animation-delay: -1.5s;
}

.confetti-piece:nth-child(5n) {
    background: #96ceb4;
    animation-delay: -2s;
}

@keyframes confetti-fall {
    0% {
        transform: translateY(-100vh) rotate(0deg);
        opacity: 1;
    }
    100% {
        transform: translateY(100vh) rotate(720deg);
        opacity: 0;
    }
}

/* Loading states */
.loading {
    opacity: 0.6;
    pointer-events: none;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .team-photo-display {
        width: 80px;
        height: 80px;
    }

    .team-photo-thumb {
        width: 50px;
        height: 50px;
    }

    .score-breakdown {
        font-size: 0.8rem;
        padding: 0.3rem;
    }

    .team-card {
        max-height: 240px;
    }
}

/* Large screen optimization for fitting all teams */
@media (min-width: 1400px) {
    .col-xl-3 {
        flex: 0 0 auto;
        width: 20%;
    }
}

/* Leaderboard specific optimizations */
.leaderboard-container {
    max-height: 90vh;
    overflow: hidden;
}

.team-card .card-body {
    padding: 0.75rem;
}

.team-card .card-header {
    padding: 0.5rem;
}

.team-card .card-footer {
    padding: 0.5rem;
}

/* Admin table styling */
.table td {
    vertical-align: middle;
}

.score-input {
    width: 80px;
}

/* Success animations */
@keyframes pulse {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

.success-pulse {
    animation: pulse 0.6s ease-in-out;
}

/* Default team image fallback */
.team-photo-thumb[src="/static/uploads/default-team.svg"],
.team-photo-display[src="/static/uploads/default-team.svg"] {
    background: var(--bs-secondary);
    display: flex;
    align-items: center;
    justify-content: center;
}

.team-photo-thumb[src="/static/uploads/default-team.svg"]::before,
.team-photo-display[src="/static/uploads/default-team.svg"]::before {
    content: "ðŸ‘¥";
    font-size: 2rem;
